INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (1, 'Soler Palau TD-160/100 N Silent', 'Вентиляторы серии TD разработаны специально для работы с каналами большой протяженности и в условиях повышенных требований к уровню шума в помещении. Имеют достоинства центробежных вентиляторов (большое давление на выходе) и преимущества осевых вентиляторов (большой расход воздуха, малые уровень шума и габаритные размеры). Вентиляторы серии TD предназначены для монтажа в вентиляционных каналах. Вместе с вентилятором поставляются крепежные элементы. Возможен как вертикальный, так и горизонтальный монтаж.', 9580, 338, 1);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (2, 'Soler Palau TD-350/125 Silent', 'Вентиляторы серии TD-SILENT с диагональными лопатками, изготавливаются из прочного пластика. Внутренний перфорированный корпус специальной конструкции и слой шумопоглощающего материала, значительно снижают уровень шума от вентилятора. Вентиляторы комплектуются монтажными кронштейнами с быстроразъемными хомутами, это позволяет извлекать корпус вентилятора для обслуживания, без демонтажа воздуховодов.', 17830, 188, 1);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (3, 'DunLi EC102-B310-033 137-006-B-004 A-0', 'Серия надежных, энергоэффективных центробежных ЕС-вентиляторов от высокотехнологичного китайского производителя Dunli. Предназначен для различного климатического оборудования. Вентиляторы Dunli отвечают самым современным требованиям в сфере создания энергосберегающей системы вентиляции: обеспечивают экономию энергии до 35 % в сравнении с АС-двигателями.', 16820, 132, 2);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (4, 'Soler Palau VMC-MINIFAN HI SIP', 'Вытяжные вентиляторы серии MINIFAN разработаны для обслуживания сразу нескольких помещений. Вентиляторы имеют 4 всасывающих патрубка и один вытяжной (для выброса воздуха на улицу), все диаметром 125мм. Могут устанавливаться за подвесным потолком, в стенных шкафах, на чердаках и других подсобных помещениях.', 68800, 35, 4);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (5, 'Soler Palau TH 800', 'Крышные вентиляторы серии TH с диагональными лопатками предназначены для работы в вытяжных системах вентиляции, но, при необходимости, могут быть переоборудованы для работы на приток.', 34980, 79, 3);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (6, 'Soler Palau TD 250/100', 'Вентиляторы серии TD разработаны специально для работы с каналами большой протяженности и в условиях повышенных требований к уровню шума в помещении. Имеют достоинства центробежных вентиляторов (большое давление на выходе) и преимущества осевых вентиляторов (большой расход воздуха, малые уровень шума и габаритные размеры). Вентиляторы серии TD предназначены для монтажа в вентиляционных каналах. Вместе с вентилятором поставляются крепежные элементы. Возможен как вертикальный, так и горизонтальный монтаж.', 11200, 115, 1);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (7, 'ВР 280-46 №3,15 1,5 KW', 'Макс. расход воздуха (м3/ч): 3400 Присоединительный диаметр (мм): 315 Страна производитель: Россия Класс защиты IP: IP54 Потребляемая мощность (Вт): 1500 Макс. рабочая температура (°С): 80', 84400, 24, 2);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (8, 'Soler Palau OZEO H ST 2', 'Мультизональный вентилятор OZEO H ST 2 предназначен для использования в частных или коммерческих зданиях с несколькими изолированными помещениями. Вытяжка воздуха организуется из помещений кухни, санузлов, ванных комнат и т.д., а свежий воздух подается в помещения гостиных, спален, кабинетов, через приточные оконные или настенные клапаны (в комплект не входят). Вентилятор комплектуется однофазным односкоростным электродвигателем и предназначен для постоянной работы.', 20261, 95, 4);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (9, 'Ровен ВЦ-14-46-5,0-О-РВ-4,0/1000/220-380 Л0', 'Вентилятор среднего давления. Лопатки, загнутые вперед. Корпус вентилятора из оцинкованной стали. Рама вентилятора из оцинкованной стали (№ 2,0-4,0) или из углеродистой стали окрашенной (№ 5,0-8,0). Трехфазный асинхронный двигатель (380 В). Конструктивная схема 1 по ГОСТ 5976-90. Класс защиты двигателя IP54.', 92315, 13, 2);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (10, 'Soler Palau OZEO E ECOWATT 2 RF', 'Материал: Пластик. Макс. расход воздуха (м3/ч): 380/260/150. Страна производитель: Испания. Цвет: Белый. Макс. рабочая температура (°С): 45. Двигатель постоянного тока: Да.', 39991, 63, 4);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (11, 'DunLi EC72-B225-004 092-007-B-001', 'Серия надежных, энергоэффективных центробежных ЕС-вентиляторов от высокотехнологичного китайского производителя Dunli. Предназначен для различного климатического оборудования. Вентиляторы Dunli отвечают самым современным требованиям в сфере создания энергосберегающей системы вентиляции: обеспечивают экономию энергии до 35 % в сравнении с АС-двигателями.', 47560, 105, 2);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (12, 'Soler & Palau TD EVO-200', 'TD-EVO - это новая серия низкопрофильных круглых канальных вентиляторов с диагональными лопатками. Модельный ряд состоит из нескольких типоразмеров с максимальным расходом воздуха от 210 до 1840 м3/ч. Оптимизированная конструкция крыльчатки, направляющего аппарата и обтекателя на выходе воздуха позволяют увеличить производительность и снизить уровень шума вентилятора.', 23860, 138, 1);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (13, 'Soler Palau CTB/4-1300/315 ECOWATT', 'Круглые канальные вентиляторы TDECOWATT комплектуются высокоэффективными электрокоммутируемыми двигателями и крыльчатками с диагональными лопатками. Корпуса и крыльчатки вентиляторов от 160 до 800 модели изготавливаются из высококачественного пластика, у моделей 1300 и 2000 корпуса - из стали, крыльчатки - из алюминия. Конструкция корпуса вентилятора позволяет извлекать блок двигатель крыльчатка без демонтажа воздуховодов.', 101446, 0, 3);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (14, 'Soler Palau CTHT/4-250', 'Серия крышных центробежных вентиляторов с горизонтальным выбросом воздуха CTHB / CTHT разработана специально для систем дымоудаления(1). Также, все вентиляторы могут работать в продолжительном режиме при температуре перемещаемого воздуха до 120°C*, а модели 140, 180, 200 и 225 до 200°С*. Основание вентиляторов изготовлено из оцинкованной стали, крышка - из алюминия. Все вентиляторы оснащены защитной решеткой на нагнетательной стороне.', 113594, 11, 3);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (15, 'Soler Palau TPSB/2-250/084', 'Низкопрофильные крышные вентиляторы с горизонтальным выбросом воздуха серии TPSB предназначены для организации вытяжной вентиляции из различных бытовых и общественных помещений. Высокоэффективные центробежные рабочие колеса с загнутыми назад лопатками, основание электродвигателя и крышка вентилятора изготавливаются из высокопрочного литого пластика. Основание выполнено из листовой стали, защищено от коррозии катафоретическим покрытием и черной полиэфирной краской.', 40158, 80, 3);
INSERT INTO vts.items (id, title, description, price, quantity, category_id) VALUES (16, 'Soler Palau OZEO-E ECOWATT RF', 'Мультизональные вентиляторы серии OZEO-E предназначены для использования в частных или коммерческих зданиях с несколькими изолированными помещениями. Вытяжка воздуха организуется из помещений кухни, санузлов, ванных комнат и т.д., а свежий воздух подается в помещения гостиных, спален, кабинетов, через приточные оконные или настенные клапаны. Вентилятор состоит из пластикового корпуса с четырьмя всасывающими патрубками и одним нагнетательным, все патрубки имеют диаметр 125 мм.', 32671, 91, 4);